import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { findActivityThunk } from "../../services/activities-thunks";   // import the thunk
import ActiviteItem from "./activities-item";

const ActivitiesList = () => {
    const { activities, loading } = useSelector(         // grab tuits and loading flag from reducer
        state => state.activityData)
    const dispatch = useDispatch();
    useEffect(() => {                               // on component load
        dispatch(findActivityThunk())                  // invoke find tuits thunk to fetch tuits and
    }, [])                                          // put them in the reducer's store so we can
    return (                                        // extract them with useSelector() and render the tuits here
        <ul className="list-group">
            {
                loading &&
                <li className="list-group-item">
                    Loading...
                </li>
            }{
                activities && activities.map(activity =>
                    <ActiviteItem
                        key={activity._id}
                        activity={activity}
                    />
                )
            }
        </ul>
    );
};

export default ActivitiesList;
